		function getAucItem(row) {
			var item = {};
			var cells = row.getElementsByTagName("td");
			item.nameLink = cells.item(1).getElementsByTagName("a").item(0);
			item.name = cells.item(1).getElementsByTagName("a").item(0).innerHTML;
			item.seller = cells.item(2).getElementsByTagName("a").item(0).innerHTML;
			item.bidder = cells.item(3).getElementsByTagName("a").length === 0 ? cells.item(3).innerHTML : cells.item(3).getElementsByTagName("a").item(0).innerHTML;
			item.current = cells.item(4).innerHTML;
			item.buyout = cells.item(5).innerHTML;
			item.ends = cells.item(6).innerHTML;
			return item;
		}

		//FixMe
		function searchItemByName(name) {
			var eqlistLength = eqlist.length;
			for (var i = 0; i < eqlistLength; i++) {
				var item = eqlist[i];
				if (item["shortDescription"] === name) return item
			}
			var runeName = name.replace("a ","") + " Rune";
			for (var i = 0; i < eqlistLength; i++) {
				var item = eqlist[i];
				if (item["shortDescription"] === runeName) return item
			}
			return undefined
	    }

		function humanize(code, value) {
			var s = value > 0 ? "+" : "";
			if (affWithValue[code]) {
				return s + value + " " + affWithValue[code];
			}
			if (affWithoutValue["code"]) {
				return s + affWithoutValue["code"];
			}
			return s + value + " " + code;
		}
		
		function drawAffects(item) {
			var div = document.createElement('div');
			div.style.width = "15%"
			div.style.display = "flex";
			div.style.flexDirection = "column";
			var affects = item["affects"];
			for(affectCode in affects) {
				var affDiv = document.createElement('div');
				affDiv.style.textAlign = "left";
				affDiv.innerHTML = humanize(affectCode, affects[affectCode]);
				div.appendChild(affDiv);
			}
			return div;
		}
		
		function drawPoppers(item) {
			var div = document.createElement('div');
			div.style.paddingLeft = "10px";
			div.style.display = "flex";
			div.style.flexDirection = "column";
			var poppers = item["poppers"];
			var size = poppers.length;
			for(var i=0; i<size; i++) {
				var popper = poppers[i];
				var restrDiv = document.createElement('div');
				restrDiv.style.textAlign = "left";
				restrDiv.innerHTML = popper["mobName"] + " {" + popper["areaName"] + "}";
				div.appendChild(restrDiv);
			}
			return div;
		}
		
		function drawCpForForges(item) {
			var div = document.createElement('div');
			div.style.paddingLeft = "10px";
			div.style.display = "flex";
			div.style.flexDirection = "column";
			var forges = item["forgesList"];
			if (!forges) {
				return undefined;
			}
			var size = forges.length;
			var titleDiv = document.createElement('div');
			titleDiv.style.textAlign = "left";
			titleDiv.innerHTML = "Cp for forges:";
			div.appendChild(titleDiv);
			for(var i=0; i<size; i++) {
				var restrDiv = document.createElement('div');
				restrDiv.style.textAlign = "left";
				restrDiv.innerHTML = forges[i];
				div.appendChild(restrDiv);
			}
			return div;
		}
	
		function drawRestricts(item) {
			var div = document.createElement('div');
			div.style.paddingTop = "5px";
			var restrAnd = item["restrAnd"];
			var restricts = item["restricts"];
			if (restrAnd === "PRIME") {
				var restrCode = Object.keys(restricts)[0];
				div.innerHTML = restricts[restrCode] + restr[restrCode] + " Prime";
			} else if (restrAnd === "GOR") {
				var r = []
				for(restrCode in restricts) {
					r.push(">" + restricts[restrCode] + restr[restrCode])
				}
				div.innerHTML = r.join(" or ");
			} else if (restrAnd === "GAND") {
				var r = []
				for(restrCode in restricts) {
					r.push(">" + restricts[restrCode] + restr[restrCode])
				}
				div.innerHTML = r.join(" and ");
			} else if (restrAnd === "LAND") {
				var r = []
				for(restrCode in restricts) {
					r.push("<" + restricts[restrCode] + restr[restrCode])
				}
				div.innerHTML = r.join(" and ");
			} else {
				div.innerHMTL = "OMG! i forgot about " + restrAnd;
			}
			return div;
		}	
	
